<h2 class="pull-left">Lista de <b>Tickets</b></h2>
<p class="links pull-right"><a href="tickets.pl" class="btn btn-xs btn-primary"><i class="glyphicon glyphicon-list"></i> Lista de Tickets</a> <a href="index.pl" class="btn btn-xs btn-primary"><span class="glyphicon glyphicon-plus"></span> Nuevo ticket</a></p>
<div class="clearfix"></div>

<div class="panel panel-default">
  <div class="panel-body">
    <form method="GET" action="tickets.pl" role="form" class="form-inline">
      <div class="row">
        <div class="form-group col-xs-8">
          <input type="text" name="q"  class="form-control input-xxlarge" placeholder="Folio ó Asunto">
        </div>
        <div class="form-group col-xs-2">
          <input type="text" name="since"  class="form-control" id="filter_since" placeholder="Fecha desde">
        </div>
        <div class="form-group col-xs-2">
          <input type="text" name="until"  class="form-control" id="filter_until" placeholder="Fecha hasta">
        </div>
        <div class="clear"></div><br><br><br>
        <div class="form-group col-xs-2">
          <select name="status"  class="form-control">
<option value="0">- Todos los status -</option>
<option value="1">Nuevo</option>
<option value="2">En proceso</option>
<option selected value="88">Nuevo y En proceso</option>
<option value="9">Resuelto</option>
</select>
        </div>
        <div class="form-group col-xs-2">
          <select name="id_requestor"  class="form-control">
<option value="0"> - Todos los solicitantes - </option>
</select>
        </div>
        <div class="form-group col-xs-2">
          <select name="priority"  class="form-control">
<option value="0">- Todas las prioridades -</option>
<option value="1">BAJA</option>
<option value="2">MEDIA</option>
<option value="3">ALTA</option>
</select>
        </div>
        <div class="form-group col-xs-2">
          <select name="ticket_type"  class="form-control">
<option value="0">- Todos los tipos -</option>
<option value="1">NUEVO</option>
<option value="2">MODIFICACION</option>
<option value="3">ERROR</option>
</select>
        </div>                            
        <div class="form-group col-xs-2">
          <select name="id_client"  class="form-control">
<option value="0"> - Todos los clientes - </option>
<option value="12">Alsuperr</option>
<option value="10">Avance y Tecnologia</option>
<option value="3">CLIENTE DEMO</option>
<option value="7">Direchisa</option>
<option value="9">Funerales Miranda</option>
<option value="8">Honorarios Médicos </option>
<option value="5">Impress Depot</option>
<option value="6">Impress Depot Producción</option>
<option value="15">Impress Depot Sami</option>
<option value="2">Mas Promocionales</option>
<option value="16">MASPROMOSTORE</option>
<option value="11">Presimap</option>
<option value="18">tester exo</option>
<option value="4">TNP Lideres en Innovacion Promocional</option>
<option value="13">Xhepl</option>
</select></div>
          <div class="form-group col-xs-2">
          <select name="comment"  class="form-control">
<option value="0">- All Comments -</option>
<option value="1">ADMIN</option>
<option value="2">CLIENTE</option>
<option value="3">NUEVO</option>
</select>
        </div><br>
        
  

        <button type="submit" class="btn btn-default"><i class="glyphicon glyphicon-search"></i> Buscar</button>
        <button type="button" class="btn btn-default" onclick="document.location='tickets.pl'"><i class="glyphicon glyphicon-refresh"></i> Limpiar</button>
      </div>
    </form>
    <div class="clearfix"></div><br />
